mapper.select:
select 

from t_HistoryIrrBRWaterStadia
where stadiaCode = #{stadiacode,jdbcType=CHAR}
  and meaTime = #{meatime,jdbcType=TIMESTAMP}

mapper.delete:
delete from t_HistoryIrrBRWaterStadia
where stadiaCode = #{stadiacode,jdbcType=CHAR}
  and meaTime = #{meatime,jdbcType=TIMESTAMP}

mapper.select:
  	SELECT t2.Stadia_Name,t1.*
	FROM (SELECT * 
			FROM t_HistoryIrrBRWaterStadia
			WHERE meaTime <= #{endTime,jdbcType=CHAR} AND meaTime >= #{beginTime,jdbcType=CHAR} AND stadiaCode = #{stadiaCode,jdbcType=CHAR}
			)t1
	LEFT JOIN t_IrrBRStadia t2
	ON t1.stadiaCode = t2.stadiaCode AND t1.stadiaCode = #{stadiaCode,jdbcType=CHAR} AND t2.stadiaCode = #{stadiaCode,jdbcType=CHAR}
